frame_eqs_eel:
wave_r = 0.5 + 0.5*sin(6*time);
wave_g = 0.5 + 0.5*sin(4.1*time);
wave_b = -1 + (1-wave_r + 1-wave_g);
warp = 0;
wave_x = wave_x + 0.1*cos(1.2*time)*sin(1.1*time)+0.1*sin(0.61*time);
wave_y = wave_y + 0.1*sin(1.2*time)*cos(4.1*time)+0.11*cos(1.1*time);

ob_r = 1-abs(wave_r);
ob_b = 1-abs(wave_b);
ob_g = 1-abs(wave_g);

pfthresh = above(bass_att,pfthresh)*2+(1-above(bass_att,pfthresh))*((pfthresh-1.3)*0.96+1.3);
pfdx_r = equal(pfthresh,2)*0.015*sin(5*time)+(1-equal(pfthresh,2))*pfdx_r;
pfdy_r = equal(pfthresh,2)*0.015*sin(6*time)+(1-equal(pfthresh,2))*pfdy_r;
rg1 = abs(sin(time));
rg2 = 0*above(rg1,0.75) + 3*below(rg1,0.25) + 1*above(rg1,0.25)*below(rg1,0.5)+
2*above(rg1,0.5)*below(rg1,0.75);
rg3 = if(equal(pfthresh,2),rg2,rg3);

echo_orient = rg3;

pixel_eqs_eel:
thresh = above(bass_att,thresh)*2+(1-above(bass_att,thresh))*((thresh-1.3)*0.96+1.3);
dx_r = equal(thresh,2)*0.015*sin(5*time)+(1-equal(thresh,2))*dx_r;
dy_r = equal(thresh,2)*0.015*sin(6*time)+(1-equal(thresh,2))*dy_r;

xbar = above(sin(dx_r*2*time),0)*abs(4-x*tan(4*cos(y*time))) +
below(sin(dx_r*2*time),0)*sin(3*x*sin(3*cos(dx_r*y*time)));
ybar = above(cos(dy_r*1.2*time),0)*abs(4-y*tan(4*cos(x*time))) +
below(cos(dy_r*1.2*time),0)*sin(3*y*sin(3*cos(dy_r*x*time)));

dx = dx + dx_r*cos(1.6*xbar);
dy = dy + dy_r*cos(1.6*ybar);
zoom = zoom - 0.01*(3*(xbar/ybar));
rot = rot + 0.01*(2*(ybar+xbar))*cos(1.52*time);
cx = cx + xbar*2*dx_r;
cy = cy + ybar*2*dy_r;
