comp:
shader_body { 
 vec2 uv2_1;
 uv2_1 = (uv + (vec2(1.0, 0.0) * texsize.zw));
 float tmpvar_2;
 tmpvar_2 = ((texture (sampler_main, uv2_1).x + (
   ((texture (sampler_blur1, uv2_1).xyz * scale1) + bias1)
 .x * 0.3)) + ((
   (texture (sampler_blur2, uv2_1).xyz * scale2)
  + bias2).x * 0.1));
 uv2_1 = (uv + (vec2(-1.0, 0.0) * texsize.zw));
 float tmpvar_3;
 tmpvar_3 = ((texture (sampler_main, uv2_1).x + (
   ((texture (sampler_blur1, uv2_1).xyz * scale1) + bias1)
 .x * 0.3)) + ((
   (texture (sampler_blur2, uv2_1).xyz * scale2)
  + bias2).x * 0.1));
 uv2_1 = (uv + (vec2(0.0, 1.0) * texsize.zw));
 float tmpvar_4;
 tmpvar_4 = ((texture (sampler_main, uv2_1).x + (
   ((texture (sampler_blur1, uv2_1).xyz * scale1) + bias1)
 .x * 0.3)) + ((
   (texture (sampler_blur2, uv2_1).xyz * scale2)
  + bias2).x * 0.1));
 uv2_1 = (uv + (vec2(0.0, -1.0) * texsize.zw));
 vec3 tmpvar_5;
 tmpvar_5.z = 0.08;
 tmpvar_5.x = (tmpvar_2 - tmpvar_3);
 tmpvar_5.y = (tmpvar_4 - ((texture (sampler_main, uv2_1).x + 
   (((texture (sampler_blur1, uv2_1).xyz * scale1) + bias1).x * 0.3)
 ) + (
