comp_hlsl:
shader_body
{
    float2 orig = uv;

    float inv_zoom = min(aspect.x,aspect.y)*0.8;
    //float t = saturate(time*0.33);
    //t = t*t*(3 - 2*t);
    //inv_zoom = lerp(1, inv_zoom, t);

    uv -= 0.5;
    uv *= inv_zoom;
    uv *= aspect.xy;

    float2 uv2;  
    uv2.x = uv.x*cos(q1) - uv.y*sin(q1);
    uv2.y = uv.x*sin(q1) + uv.y*cos(q1);
    uv2 *= aspect.zw;
    uv2 += 0.5;

    ret = abs( tex2D(sampler_main, uv2).xyz*2.65
                            + GetBlur1(uv2)*-2 );

    ret.xyz *= 1.5;
}
