frame_eqs_eel:
//Unchained's Beat Code

old_bass_flop=bass_flop;
old_treb_flop=treb_flop;
old_mid_flop=mid_flop;
chaos=.9+.1*sin(pulse);
entropy=if(equal(pulse,-20),1+bass_flop+treb_flop+mid_flop+rand(2),entropy);
bass_thresh = above(bass_att,bass_thresh)*2 + (1-above(bass_att,bass_thresh))*((bass_thresh-1.3)*chaos+1.3);
bass_flop=abs(bass_flop-equal(bass_thresh,2));
treb_thresh=above(treb_att,treb_thresh)*2 + (1-above(treb_att,treb_thresh))*((treb_thresh-1.3)*chaos+1.3);
treb_flop=abs(treb_flop-equal(treb_thresh,2));
mid_thresh=above(mid_att,mid_thresh)*2 + (1-above(mid_att,mid_thresh))*((mid_thresh-1.3)*chaos+1.3);
mid_flop=abs(mid_flop-equal(mid_thresh,2));
bass_changed=bnot(equal(old_bass_flop,bass_flop));
mid_changed=bnot(equal(old_mid_flop,mid_flop));
treb_changed=bnot(equal(old_treb_flop,treb_flop));
bass_residual = bass_changed*sin(pulse*.1*entropy) + bnot(bass_changed)*bass_residual;
treb_residual = treb_changed*sin(pulse*.1*entropy) + bnot(treb_changed)*treb_residual;
mid_residual = mid_changed*sin(pulse*.1*entropy) + bnot(mid_changed)*mid_residual;
pulse=if(above(abs(pulse),20),-20,pulse+(bass_thresh+mid_thresh+treb_thresh)*.032);
q1=mid_residual;
q2=bass_residual;
q3=treb_residual;
q4=sin(pulse);
q5=cos(pulse/2+q1);

q6=sin(q3*q1+q7*q2);
q7=above(q1,0)+above(q2,0)+above(q3,0)+above(q3,0)*treb_flop+above(q2,0)*bass_flop+above(q1,0)*mid_flop;
q8=entropy;

vol=(bass+mid+treb_att)*.3333;

redsine=.5+.15*bass*sin(time*3);
greensine=.5+.15*mid*sin(time*2);
bluesine=.5+.15*treb*sin(time);

redif=if(above(bass,1.2),redsine,if(above(redif,.95),.0,redif*.95));
greenif=if(above(mid,1.2),greensine,if(above(greenif,.95),.0,greenif*.95));
blueif=if(above(treb,1.2),bluesine,if(above(blueif,.95),.0,blueif*.95));

r=redif;
g=greenif;
b=blueif;

q9=r;
q10=g;
q11=b;

wave_r=wave_r*(sin(redif*cos(b+g)));
wave_g=wave_g*(sin(greenif*cos(r+b)));
wave_b=wave_b*(sin(blueif*cos(r+g)));

ib_r=r;
ib_g=g;
ib_b=b;

ob_r=wave_r*redif;
ob_g=wave_g*greenif;
ob_b=wave_b*blueif;

pixel_eqs_eel:
//xy transform code
xv = sin(time*.333)*x -cos(time*.667)*x;
yv = cos(time*.233)*y +sin(time*.567)*y;
x = xv; y = yv;
//end x/y transform code

sx=sx+.075*sin(x*3+q7*x);
sy=sy+.075*sin(y*3.1+q7*y);

dx=dx+.005*cos(y*20+.1*cos(time*.11)+(q2*q6));
dy=dy+.005*sin(x*20+.1*sin(time*.09)+(q3*q6));

