comp:
shader_body { 
 vec2 uvv_1;
 vec2 uvm_2;
 uvm_2.x = ((0.5 * texture (sampler_noise_hq, uv.xx)) + (0.5 * texture (sampler_main, uv.xx))).x;
 uvm_2.y = ((0.5 * texture (sampler_main, uv.yy)) + (0.5 * texture (sampler_noise_hq, uv.yy))).x;
 uvv_1 = (uv * uvm_2);
 vec3 tmpvar_3;
 tmpvar_3 = (((texture (sampler_blur1, uv).xyz * scale1) + bias1) - ((texture (sampler_blur3, texture (sampler_noise_hq, 
   ((texture (sampler_blur3, uvv_1).xyz * scale3) + bias3)
 .xy).xy).xyz * scale3) + bias3));
 vec2 tmpvar_4;
 tmpvar_4 = fract((tmpvar_3.xy + uvm_2));
 vec4 tmpvar_5;
 tmpvar_5.w = 1.0;
 tmpvar_5.xyz = ((2.0 * tmpvar_3) * texture (sampler_main, (tmpvar_4 * (uvv_1 - tmpvar_4))).xyz);
 ret = tmpvar_5.xyz;
}
