frame_eqs_eel:
basstime = basstime + bass*0.03;
q1 = basstime*4;

//start in most active range
basstime = if(below(basstime,1000),1000,basstime);
//basstime = if(above(basstime,495),450,basstime);

basstime = basstime + bass_att*0.03;
vol = pow(bass+mid+treb,2);
basssum = vol;

stickybit = time%2;

//avg vol
//buffer 1
volAvg = volAvg + vol*equal(stickybit,1);
//number of samples 1 or framerate
sample1 = sample1 + equal(stickybit,1);
//buffer 2
volAvg2 = volAvg2 + vol*equal(stickybit,0);
//number of samples 2
sample2 = sample2 + equal(stickybit,0);

//transision
edge = bnot(equal(bit2,stickybit));

volAvg = volAvg - volAvg*edge*stickybit;
volAvg2 = volAvg2 - volAvg2*edge*equal(stickybit,0);

sample1 = sample1  - sample1*edge*stickybit;
sample2 = sample2  - sample2*edge*equal(stickybit,0);

//test vol against avg buffer lvl
diff = if(equal(stickybit,1), (basssum/(volAvg2/sample2)) , 0);
diff = if(equal(stickybit,0), (basssum/(volAvg/sample1)), diff);

q3 = diff;

bit2 = time%2;

difftime = difftime + diff*0.03;
q2 = difftime;

//fix a strange error
difftime = if(above(difftime,2000),0, difftime);

monitor = q3;
