waves.point_eqs_eel:
//q1 = state;
//q2 = basstime;

q1 = 0;
speed = bass_att*0.3;
v = sample*10000 + value2*bass*0.1;

//state 0 elipse
xs = xs + sin(v*0.1 + sin(v)*0.1)*speed*atan(v*1.51);
ys = ys + sin(v*0.1 + cos(v)*0.1)*speed*atan(v);

x = 0.5 + 0.5*sin(xs*0.1)*cos(time*1 + ys);
y = 0.5 + 0.5*sin(ys*0.1)*cos(time*1.1 + xs);


r = 0.5 + 0.5*sin(q1*0.22);
g = 0.5 + 0.5*sin(q1*0.307) + x;
//b = 0.4 + 0.4*sin(time*0.959);


xs = if(above(xs,1000),0 ,xs);
ys = if(above(ys,1000),0 ,ys);

//apply zoom burst on beat
flag = above(q3,2);
//state zero is ready state
state = state + flag*equal(state,0);

//upwards flight
yval = if(equal(state,0),0, yval);
yval = yval + 0.00004*equal(state,1);
ymax = if(equal(state,0),rand(100)*0.004,ymax);

//hit apogee
state = if(above(yval,0.5+ymax),2,state);

//reset variable
yval = if(equal(state,0),0, yval);
burst = if(equal(state,0),0.001,burst);
alphax = if(equal(state,0),1,alphax);
xdriftinc = if(equal(state,0),0,xdriftinc);

burstspeed = if(equal(state,0),rand(10),burstspeed);
xdrift = if(equal(state,0),rand(20),xdrift);
//to get the numbers small enough have to use constants
xdriftinc = xdriftinc
+ equal(xdrift,0)*equal(state,1)*0.00002
+ equal(xdrift,1)*equal(state,1)*0.000018
+ equal(xdrift,2)*equal(state,1)*0.000016
+ equal(xdrift,3)*equal(state,1)*0.000014
+ equal(xdrift,4)*equal(state,1)*0.000012
+ equal(xdrift,5)*equal(state,1)*0.00001
+ equal(xdrift,6)*equal(state,1)*0.000008
+ equal(xdrift,7)*equal(state,1)*0.000006
+ equal(xdrift,8)*equal(state,1)*0.000004
+ equal(xdrift,9)*equal(state,1)*0.000002
+ equal(xdrift,11)*equal(state,1)*-0.000002
+ equal(xdrift,12)*equal(state,1)*-0.000004
+ equal(xdrift,13)*equal(state,1)*-0.000006
+ equal(xdrift,14)*equal(state,1)*-0.000008
+ equal(xdrift,15)*equal(state,1)*-0.00001
+ equal(xdrift,16)*equal(state,1)*-0.000012
+ equal(xdrift,17)*equal(state,1)*-0.000014
+ equal(xdrift,18)*equal(state,1)*-0.000016
+ equal(xdrift,19)*equal(state,1)*-0.000018;

burst = burst
+ equal(burstspeed,0)*equal(state,2)*0.000024
+ equal(burstspeed,1)*equal(state,2)*0.000022
+ equal(burstspeed,2)*equal(state,2)*0.00002
+ equal(burstspeed,3)*equal(state,2)*0.000018
+ equal(burstspeed,4)*equal(state,2)*0.000016
+ equal(burstspeed,5)*equal(state,2)*0.000014
+ equal(burstspeed,6)*equal(state,2)*0.000012
+ equal(burstspeed,7)*equal(state,2)*0.00001
+ equal(burstspeed,8)*equal(state,2)*0.000008
+ equal(burstspeed,9)*equal(state,2)*0.000008;

alphax = alphax - 0.00004*equal(state,2);
state = if(below(alphax,0),0,state);


s = sample*3.14*100;
ss = sample*6.28*1000;

//draw torus
xs = (burst + 0.0001*cos(s))*cos(ss);
ys = (burst + 0.0001*cos(s))*sin(ss);
zs = 0.1*burst*sin(s);


//rotate x axis
angle = sin(-q1*0.035)*6.28;
yx = ys*cos(angle) - zs*sin(angle);
zx = ys*sin(angle) + zs*cos(angle);
xx = xs;

//rotate y axis
angle2 = sin(-q1*0.03)*6.28;
xd = xx*cos(angle2) - zx*sin(angle2);
zd = xx*sin(angle2) + zx*cos(angle2);
yd = yx;

//rotaye z axis
angle3 = sin(-q1*0.022)*6.28;
xn = xd*cos(angle3) - yd*sin(angle3);
yn = xd*sin(angle3) + yd*cos(angle3);

zd = zd + 2;

x = xn*zd*0.3 + 0.5;
y = yn*zd*0.3*1.2;


x = x + xdriftinc;
y = y + sin(yval*1.5) - 0.18;


r = 0.5 + 0.5*sin(q1*1.3 + x + x);
g = 0.5 + 0.5*sin(q1*1.1 + x + y);
b = 0.5 + 0.5*sin(q1*1.66 + y + y);

frame_eqs_eel:
basstime = basstime + bass*0.06;
q1 = basstime;

basstime = if(below(basstime,1000),1000,basstime);

vol = pow(bass+mid+treb,2);
basssum = vol;

stickybit = time%2;

//avg vol
//buffer 1
volAvg = volAvg + vol*equal(stickybit,1);
//number of samples 1 or framerate
sample1 = sample1 + equal(stickybit,1);
//buffer 2
volAvg2 = volAvg2 + vol*equal(stickybit,0);
//number of samples 2
sample2 = sample2 + equal(stickybit,0);

//transision
edge = bnot(equal(bit2,stickybit));

volAvg = volAvg - volAvg*edge*stickybit;
volAvg2 = volAvg2 - volAvg2*edge*equal(stickybit,0);

sample1 = sample1  - sample1*edge*stickybit;
sample2 = sample2  - sample2*edge*equal(stickybit,0);

//test vol against avg buffer lvl
diff = if(equal(stickybit,1), (basssum/(volAvg2/sample2)) , 0);
diff = if(equal(stickybit,0), (basssum/(volAvg/sample1)), diff);

q3 = diff;

bit2 = time%2;

difftime = difftime + diff*0.03;
q2 = difftime;

//fix a strange error
difftime = if(above(difftime,2000),0, difftime);


monitor = q1;

warp:
shader_body { 
 vec2 uv_1;
 vec3 color_2;
 vec2 uv2_3;
 uv2_3 = (uv + q3);
 color_2 = ((texture (sampler_noise_lq, uv2_3) - 0.5).xyz + (texture (sampler_noise_mq, uv2_3) - 0.5).xyz);
 color_2 = (color_2 + (texture (sampler_noise_hq, uv2_3) - 0.5).xyz);
 uv_1 = (uv + (color_2 * 0.01).xy);
 uv_1.y = (uv_1.y + (abs(
   (color_2 * 0.01)
 ) + (bass_att * 0.01)).x);
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = (texture (sampler_main, ((uv_1 * 0.95) + (
   ((texture (sampler_blur1, ((
     (uv_1 - 0.5)
    * 0.9) + 0.5)).xyz * scale1) + bias1)
  * 0.1).xy)).xyz - 0.01);
 ret = tmpvar_4.xyz;
}

comp:
