init_eqs_eel:
db=.01;
beatrate=1;
avgbeatrate=2;
lbbtime=time;
avgdb=.01;
vol=1;
avgvol=.2;
phase=0;
bool=0;
const=.02;
mtime=150+bass_att*1000;
t3=200+treb_att*1000;
t4=100+mid_att*1000;

frame_eqs_eel:
db=(bass-lbass)*fps;
lbass=bass;
avgdb=avgdb*.99+abs(db)*.01;
avgvol=avgvol*.99+(bass+mid+treb)*.0033;

//beatdection
rawbeatb=above(abs(db),avgdb*avgvol*4);
beatb=rawbeatb*above(time-lbbtime,avgbeatrate*.5);
beathard=beatb*(abs(db)-avgdb*4);
//beatrate calc
beatrate=beatb*(time-lbbtime)+(1-beatb)*beatrate;
avgbeatrate=beatb*(avgbeatrate*.9+beatrate*.1)+(1-beatb)*avgbeatrate;
lbbtime=time*beatb+(1-beatb)*lbbtime;

ph=(time-lbbtime)*(60/avgbeatrate)*const;
phase=max(min( ph, 1 ),0);

//const=beatb*( bool*(const-.001)+(1-bool)*(const+.001) ) + (1-beatb)*const;
const=const*(1-beatb)+beatb*( const+ (.01*bool) );

bool=below(phase,.98)*(.99-ph)-above(phase,.98)*(ph*.5);
monitor=const;
bc=bc+beatb;

vol=(rawbeatb*(db-(avgdb))*.01);
avgvol=avgvol*.999+vol*.01;

mtime=mtime+min(avgvol*.5,.25)*(60/fps);

decay=.9999;
warp=0;
wrap=1;


// This is the version you should use...
// ----------- start copying -----------
hue=(mtime*.01+phase*.5); // change this
h=6*(hue-int(hue));
sw1=below(h,1); sw2=(1-sw1)*below(h,2); sw3=(1-sw1)*(1-sw2)*below(h,3); sw4=(1-sw1)*(1-sw2)*(1-sw3)*below(h,4);
sw6=above(h,5); sw5=(1-sw1)*(1-sw2)*(1-sw3)*(1-sw4)*(1-sw6);
ob_r=sw1+sw2*(2-h)+sw5*(h-4)+sw6;  // resulting Red value, assign it to q1
ob_g=sw1*h+sw2+sw3+sw4*(4-h);      // resulting Green value, assign it to q2
ob_b=sw3*(h-2)+sw4+sw5+sw6*(6-h);  // resulting Blue value, assign it to q3
// ----------- end ----------------
ob_a=.3+phase*.6;

q8=phase;
t3=t3+min(avgvol*.0005*treb*(60/avgbeatrate),.3)*(60/fps);
t4=t4+min(avgvol*.0005*bass*(60/avgbeatrate),.3)*(60/fps);
q3=t3;
q4=t4;
cx=sin((t3+t4)*.01)*.4+.5;
cy=sin((mtime+t3)*.01)*.4+.5;

q6=cx;
q7=cy;

q5=mtime;

pixel_eqs_eel:
pi=3.1415;
zmx=1+sin(q3+mtime)*.05;
zmy=1+sin(q4-mtime)*.05;

sx=zmx;
sy=zmy;

ax=ang;
ay=ang;

mrot=sin(q5*.01)*.5;
//rot=(1-mrot);
dx= sin(ax)*mrot*zmx*.1;
dy= (cos(ay)*mrot)*zmy*.1;
