frame_eqs_eel:
warp=0;
ib_a =0.2*bass;
wave_r = wave_r + 0.45*(0.5*sin(time*0.701)+ 0.3*cos(time*0.438));
wave_b = wave_b - 0.4*(0.5*sin(time*4.782)+0.5*cos(time*0.522));
wave_g = wave_g + 0.4*sin(time*1.731);
decay = decay - equal(frame%100,0)*0.1;
vol = 0.167*(bass+mid);
xamptarg = if(equal(frame%15,0),min(0.5*vol*bass_att,0.5),xamptarg);
xamp = xamp + 0.5*(xamptarg-xamp);
xdir = if(above(abs(xpos),xamp),-sign(xpos),if(below(abs(xspeed),0.1),2*above(xpos,0)-1,xdir));
xspeed = xspeed + xdir*xamp - xpos - xspeed*0.055*below(abs(xpos),xamp);
xpos = xpos + 0.001*xspeed;
wave_x = 1.5*xpos + 0.5;
yamptarg = if(equal(frame%15,0),min(0.3*vol*treb_att,0.5),yamptarg);
yamp = yamp + 0.5*(yamptarg-yamp);
ydir = if(above(abs(ypos),yamp),-sign(ypos),if(below(abs(yspeed),0.1),2*above(ypos,0)-1,ydir));
yspeed = yspeed + ydir*yamp - ypos - yspeed*0.055*below(abs(ypos),yamp);
ypos = ypos + 0.001*yspeed;
wave_y = 1.5*ypos + 0.5;
zoom = .995;







frametest = frame%2;
wave_x = if(frametest,1-wave_x,wave_x);
wave_y = if(frametest,1-wave_y,wave_y);
wave_r = if(frametest,wave_r,wave_g);
wave_g = if(frametest,wave_g,wave_b);
wave_b = if(frametest,wave_b,wave_r);
monitor = green;
q8 =  oldq8+ 0.0005*(pow(1+1.2*bass+0.4*bass_att+0.1*treb+0.1*treb_att+0.1*mid+0.1*mid_att,6)/fps);
oldq8 = q8;

mv_g=0.5+bass_att*.1;
beat=if(above(bass*bass_att,4.5),1-beat,beat);
q1=beat*2-1;

amp =amp*.8+.2*(bass_att+mid_att+treb_att)*.3;
q2 = min(amp,1);
trebcap=trebcap*.7+.16*treb;
q3=trebcap*2;
monitor = q3;
