shader_body { 
 vec2 my_uv_1;
 vec2 tmpvar_2;
 tmpvar_2 = ((uv - 0.5) * vec2((1.0 + q1)));
 vec2 tmpvar_3;
 tmpvar_3.x = ((tmpvar_2.x * tmpvar_2.x) - (tmpvar_2.y * tmpvar_2.y));
 tmpvar_3.y = ((2.0 * tmpvar_2.x) * tmpvar_2.y);
 vec2 tmpvar_4;
 tmpvar_4.x = ((tmpvar_3.x * -0.12) - (tmpvar_3.y * 0.74));
 tmpvar_4.y = ((tmpvar_3.x * 0.74) + (-0.12 * tmpvar_3.y));
 vec2 tmpvar_5;
 tmpvar_5.x = ((tmpvar_3.x * (tmpvar_4.x + 1.0)) + (tmpvar_3.y * tmpvar_4.y));
 tmpvar_5.y = ((tmpvar_3.y * (tmpvar_4.x + 1.0)) - (tmpvar_3.x * tmpvar_4.y));
 my_uv_1 = (tmpvar_5 * (1.0/((
   ((tmpvar_4.x + 1.0) * (tmpvar_4.x + 1.0))
  + 
   (tmpvar_4.y * tmpvar_4.y)
 ))));
 vec4 tmpvar_6;
 tmpvar_6.w = 1.0;
 tmpvar_6.xyz = (texture (sampler_fc_main, (my_uv_1 - floor(my_uv_1))) * 0.94).xyz;
 ret = tmpvar_6.xyz;
