warp:
shader_body { 
 vec3 ret_1;
 ret_1 = (mix (texture (sampler_fc_main, uv).xyz, (
   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1), vec3(-0.25, -0.25, -0.25)) * 0.93);
 ret_1 = (ret_1 * 0.96);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = texture (sampler_main, uv).xyz;
 ret_1 = (ret_1 * ret_1);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz;
    ret = lerp(ret, GetBlur1(uv), -0.25)*0.93;

    // darken (decay) over time
    ret *= 0.96; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret *= 1.00; //gamma
    ret *= ret; //darken
}
