#define sampler_pic sampler_cells
sampler2D sampler_pic;

shader_body
{
    ret = tex2D( sampler_main, uv ).xyz;


    // ERROR DIFFUSION DITHER - looks great
    float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
    ret += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)/256.0*1;

    // mix in img...
    float3 pic = tex2D(sampler_pic, uv_orig*aspect.xy).xyz;
    float lum = lum(pic);
    float use_it = abs(lum*0.8+0.1 - (0.5+roam_cos.y*0.25));
    use_it = saturate(1 - use_it*43);
    ret = lerp(ret, pic, use_it*0.07);    

    // darken over time
    //ret -= 0.004;
