   ((uv * texsize_noise_lq.zw) * texsize.xy)
 )) * 2.0) - 1.0);
 ret_1 = (texture (sampler_pw_main, (uv + (
   (tmpvar_2.zw * texsize.zw)
  * 0.5))).xyz + ((0.59 - 
   (0.2 * rad)
 ) * tmpvar_2.xyz));
 vec3 tmpvar_3;
 tmpvar_3 = clamp (((
   (ret_1 - 0.5)
  * 3.0) + 0.25), 0.0, 1.0);
 ret_1.x = tmpvar_3.x;
 ret_1.yz = tmpvar_3.xx;
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = ret_1;
 ret = tmpvar_4.xyz;
}

comp:
shader_body { 
 vec2 uv_1;
 vec3 ret_2;
 uv_1 = (0.05 + (0.9 * uv));
 ret_2 = (abs((
   ((texture (sampler_blur1, uv_1).xyz * scale1) + bias1)
  - texture (sampler_main, uv_1).xyz)) * 6.0);
 ret_2 = (ret_2 * 1.333);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_2;
 ret = tmpvar_3.xyz;
}

