wave_g = wave_g*sqr(.01*rand(100));
decay = .9999+.0001+0*if(equal(frame % 10,0),.95,1);
monitor = wave_g;
cx=.5+.3*cos(time*.21);
cy=.5+.3*sin(time*.1);
cx=cx+(bass-1.2)*.1;
cy=cy+(treb_att-1)*.1;
mv_r=bass_att*.4;
