pixel_eqs_eel:
near=below(sqr(q1-x)+sqr(q2-y),.04);
notnear = 1- near;
sy=max(0.3,pow(bass_att,.2))*near + notnear;
sx=1/sy*near + notnear;
rot = .02*sin(x*5+time)*notnear-.03;
zoom = 1+ notnear*.001*(.5+sin(ang*5+time));

