frame_eqs_eel:
warp = 0;

wave_b = if(below(treb, 1.8),1-.025*rand(10),0);
wave_g = wave_g*sqr(.01*rand(100));
decay = .9999+.0001+0*if(equal(frame % 10,0),.95,1);
monitor = wave_g;
cx=.5+.3*cos(time*.21);
cy=.5+.3*sin(time*.1);
cx=cx+(bass-1.2)*.1;
cy=cy+(treb_att-1)*.1;
mv_r=bass_att*.4;
wave_x = cx;
wave_y=1-cy;

q1=cx;
q2=cy;

pixel_eqs_eel:
near=below(sqr(q1-x)+sqr(q2-y),.04);
notnear = 1- near;
sy=max(0.3,pow(bass_att,.2))*near + notnear;
sx=1/sy*near + notnear;
rot = .02*sin(x*5+time)*notnear-.03;
zoom = 1+ notnear*.001*(.5+sin(ang*5+time));

warp:
shader_body { 
 vec3 ret_1;
 ret_1 = texture (sampler_main, uv).xyz;
 vec2 tmpvar_2;
 tmpvar_2 = (normalize((uv - uv_orig)) * texsize.zw);
 vec4 tmpvar_3;
 tmpvar_3.w = 0.0;
 tmpvar_3.xyz = ret_1;
 vec4 tmpvar_4;
 tmpvar_4.w = 0.0;
 tmpvar_4.xyz = max (tmpvar_3, (texture (sampler_main, (uv - tmpvar_2)) * 0.9)).xyz;
 vec4 tmpvar_5;
 tmpvar_5.w = 0.0;
 tmpvar_5.xyz = max (tmpvar_4, (texture (sampler_main, (uv + tmpvar_2)) * 0.97)).xyz;
 vec4 tmpvar_6;
 tmpvar_6.w = 0.0;
 tmpvar_6.xyz = max (tmpvar_5, (texture (sampler_main, (uv + 
   (tmpvar_2 * 2.0)
 )) * 0.97)).xyz;
 ret_1 = (max (tmpvar_6, (texture (sampler_main, 
   (uv + (tmpvar_2 * 3.0))
 ) * 0.9)).xyz * 0.92);
 vec4 tmpvar_7;
 tmpvar_7.w = 1.0;
 tmpvar_7.xyz = ret_1;
 ret = tmpvar_7.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (abs((
   ((texture (sampler_blur1, uv).xyz * scale1) + bias1)
  - texture (sampler_main, uv).xyz)) * 6.0);
 ret_1 = (ret_1 * 1.333);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}
