cx = cx + 0.210*( 0.60*sin(0.374*time) + 0.40*sin(0.294*time) );
cy = cy + 0.210*( 0.60*sin(0.393*time) + 0.40*sin(0.223*time) );
dx = dx + 0.003*( 0.60*sin(0.234*time) + 0.40*sin(0.277*time) );
dy = dy + 0.003*( 0.60*sin(0.284*time) + 0.40*sin(0.247*time) );
decay = decay - 0.01*equal(frame%6,0);
dx = dx + dx_residual;
dy = dy + dy_residual;
bass_thresh = above(bass_att,bass_thresh)*2 + (1-above(bass_att,bass_thresh))*((bass_thresh-1.3)*0.96+1.3);
dx_residual = equal(bass_thresh,2.13)*0.016*sin(time*7) + (1-equal(bass_thresh,2.13))*dx_residual;
dy_residual = equal(bass_thresh,2.13)*0.012*sin(time*9) + (1-equal(bass_thresh,2.13))*dy_residual;
wave_x = wave_x - dx_residual*7;
wave_y = wave_y - dy_residual*7;
wave_mystery = time*0.03;

zoom = zoom + 0.005*( 0.60*sin(0.1934*time+3) + 0.40*sin(0.307*time+9) );
zoom = zoom + max(0,bass_att-1.1)*0.2;
warp = warp + max(0,treb-1.1)*0.5;

warp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv);
 ret_1 = (tmpvar_2.xyz + ((tmpvar_2.xyz - 
   ((texture (sampler_blur2, uv).xyz * scale2) + bias2)
 ) * 0.6));
 ret_1 = (ret_1 * 0.9);
 ret_1 = (ret_1 + ((
   ((texture (sampler_noise_lq, ((
     (uv_orig * texsize.xy)
    * 
     (texsize_noise_lq.zw * 0.4)
   ) + rand_frame.xy)).xyz - 0.5) / 256.0)
  * 12.0) * clamp (
   (treb_att - 1.0)
 , 0.0, 1.0)));
 vec3 tmpvar_3;
 tmpvar_3 = mix (ret_1, vec3(dot (ret_1, vec3(0.32, 0.49, 0.29))), vec3(0.08, 0.08, 0.08));
 ret_1 = tmpvar_3;
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = tmpvar_3;
 ret = tmpvar_4.xyz;
}

comp:
shader_body { 
 vec2 uv_1;
 vec3 ret_2;
 uv_1 = (0.05 + (0.9 * uv));
 ret_2 = (abs((
   ((texture (sampler_blur1, uv_1).xyz * scale1) + bias1)
  - texture (sampler_main, uv_1).xyz)) * 6.0);
 ret_2 = (ret_2 * 1.333);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_2;
 ret = tmpvar_3.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // take the difference between the crisp and blurred images,
    // then add it back into the image.  Creates spots and stripes over time.
    ret += (ret - GetBlur2(uv))*0.6;
    ret *= 0.9;

   // add noise:
   float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 0.4 + rand_frame.xy;
   ret += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)/256.0 * 12 * saturate(treb_att-1);

   // desaturate over time, to keep the globs white
   ret = lerp(ret, lum(ret), 0.08);
}

