shapes.frame_eqs_eel:
i=instance;     sa=i/num_inst;

c=below(sa,.5);

x3=if(i%2,-.05,.05)+.12*sin(533*sa+.2*time);
y3=if(c,-.05,.05)+.05*sin(876*sa+.14*time);

z3a=(100*sa)-q10;
z3b =(-50+100*sa)+q10;
z3=if(c,z3a,z3b);


d=0;     zoom=20;


l = sqrt(x3*x3 + y3*y3);
w = atan2(x3,y3);
d = sqrt(x3*x3 + y3*y3 + (z3+d)*(z3+d));
p = tan(asin(1) + atan2(d+z3,l));

x1 = zoom*sin(w)*p;
y1 = zoom*cos(w)*p;

x = 0.5 + x1/q6;
y = 0.5 + y1;

rad *= .1*zoom/d;

sample=instance/num_inst;

p=0;

z=(.15+.05*sin(.16*time))*(1+4*p)*(1+.4*sin(time+10*p));

x1= z* (.3*sin(.5*q1)/(.2+p) + .3*sin(6*p+time));
y1= z* ( .3*sin(.5*q2)/(.2+p) + .3*sin(6*p-time));


///// star

p0=3;   p1=p0*sample;   p2=p1%p0;   p3=2*3.14/p0;

x=p2-p1;      y=0;

t =  20*sin(.02*time+.1*q1+.1*q2);

mx=x*sin(p2*p3+t)+y*cos(p2*p3+t);
my=y*sin(p2*p3+t)-x*cos(p2*p3+t);

d=sqrt(mx*mx+my*my);
rad=rad*(1-.6*d);

zoom=.8*z;
x=.5+(x1+zoom*mx)/q6;    y=.5+y1 + zoom*my;

rad=rad*zoom*sin(3*3.14*sample);

waves.point_eqs_eel:
//// stars

q10=123*sample;

x = -.5+sample;
y = -.5+abs(sin(q10));

t=.02*time;
mx = x*cos(t)+y*sin(t);
my = y*cos(t)-x*sin(t);

///random factor
p1=.1*sin(630*sample);
p2=.1*sin(530*sample);

x= .5+p1+mx;
y= .5+p2+my;

frame_eqs_eel:
