shapes.frame_eqs_eel:
i=instance;   sa=i/1014;    sample=((i/6)%169)/169;

c=below(sample,.5);

my_x=if((169*sample)%2,-.05,.05)+.12*sin(633*sample+.2*time);

my_y=if(c,-.035,.035)+.05*sin(853*sample+.14*time);

my_z1=(100*sample)-q10;
my_z1=if(above(0,my_z1),my_z1+50,my_z1);
my_z2=(-50+100*sample)+q10;
my_z2=if(above(my_z2,50),my_z2-50,my_z2);
my_z=if(c,my_z1,my_z2);


//// rotations

d = 0; 
zoom = 20;

w1 = 0;
w2 = 0;
w3 = 0;

x1 = cos(w1)*my_x + sin(w1)*my_y;
y1 = -sin(w1)*my_x + cos(w1)*my_y;
z1 = my_z;

x2 = cos(w2)*x1 + sin(w2)*z1;
z2 = -sin(w2)*x1 + cos(w2)*z1;
y2 = y1;

y3 = cos(w3)*y2 + sin(w3)*z2;
z3 = -sin(w3)*y2 + cos(w3)*z2;
x3 = x2;

l = sqrt(x3*x3 + y3*y3);
w = atan2(x3,y3);
d = sqrt(x3*x3 + y3*y3 + (z3+d)*(z3+d));
p = tan(asin(1) + atan2(d+z3,l));

my_x = zoom*sin(w)*p;
my_y = zoom*cos(w)*p;

x = 0.5 + my_x/q6;
y = 0.5 + my_y;






t = 169*6.28*sa + 2*d;     /// 169 cubes made of 6 triangles each
z1=.025;
x2=z1*sin(t)/d;      y2=z1*cos(t)/d;

x=x+x2/q6;       y=y+y2;

rad *= 10*z1/d;
ang = 3.14/4 + t ; 


t1=2/d; 

r=t1*(.05+.4*(i/2)%3);           r2=r;
g=r;    g2=g;
b=r;    b2=b;

waves.point_eqs_eel:
//// stars

q10=123*sample;

x = -.5+sample;
y = -.5+abs(sin(q10));

t=.004*time;
mx = x*cos(t)+y*sin(t);
my = y*cos(t)-x*sin(t);

///random factor
p1=.1*sin(630*sample);
p2=.1*sin(530*sample);

x= .5+p1+mx;
y= .5+p2+my;

