frame_eqs_eel:
wave_r = 0.5 + 0.5*sin(6*time);
wave_g = 0.5 + 0.5*sin(4.1*time);
wave_b = -1 + (1-wave_r + 1-wave_g);
warp = 0;

ob_r = 1-abs(wave_r);
ob_g = 1-abs(wave_g);
ob_b = 1-abs(wave_b);
ib_r = abs(wave_r);
ib_b = abs(wave_b);
ib_g = abs(wave_g);
;

pixel_eqs_eel:
mekdx=dx;//////////////////////////////////////////
kdy=dy;
ksx=sx;
ksy=sy;
krt=rot;
kzm=zoom;
kcx=cx;
kcy=cy;
kwp=warp;

dx=0;
dy=0;
sx=1;
sy=1;
rot=0;
zoom=1;
cx=.5;
cy=.5;
warp=0;/////////////////////////////////////////////////////

thresh = above(bass_att,thresh)*2+(1-above(bass_att,thresh))*((thresh-1.3)*0.96+1.3);
dx_r = equal(thresh,2)*0.015*sin(5*time)+(1-equal(thresh,2))*dx_r;
dy_r = equal(thresh,2)*0.015*sin(6*time)+(1-equal(thresh,2))*dy_r;

xy = above(sin(time),0)*x + below(sin(1.34*time),0)*y + above((bass_att),1.1)*-1;

dx = dx + dx_r;
dy = dy + dy_r;
sx = sx - abs(12*dx_r*(xy*sin(xy*bass_att*1.25)));
sy = sy - abs(12*dy_r*(xy*sin(xy*bass_att*1.25)));
rot = rot + 22*dx_r*(xy*cos(4*xy*treb_att));
cx = cx + 8*dx_r*(xy*sin(xy*mid_att*1.1));
cy = cy + 8*dy_r*(xy*sin(xy*mid_att*1.1));
///////////////////////////////////////
cr1=1*.01;
cr2=99*.01;; ; ; ;
crx=cr2-cr1;
ci=(1-(1-abs(-2*(x+y)/2+1)+(1-abs(-2*(1-(x+(1-y))/2)+1)))/2)*4-1;
cb=.25  ;
crs=2;
cr=pow(sin(ci*6.3-(cb*6.3))/2+.5,crs)*crx+cr1;
cqr=if( above (ci,.25+cb),cr,crx+cr1);crr=if( below(ci,1.5),cqr,crx+cr1);
jx1=crr;
kx1=1-crr;

jdx=dx;
jdy=dy;
jsx=sx;
jsy=sy;
jrt=rot;
jzm=zoom;
jcx=cx;
jcy=cy;
jwp=warp;

dx=(jx1*jdx+kx1*kdx);
dy=(jx1*jdy+kx1*kdy);
sx=(jx1*jsx+kx1*ksx);
sy=(jx1*jsy+kx1*ksy);
rot=(jx1*jrt+kx1*krt);
zoom=(jx1*jzm+kx1*kzm);
cx=(jx1*jcx+kx1*kcx);
cy=(jx1*jcy+kx1*kcy);
warp=(jx1*jwp+kx1*kwp);////////////////////////////////////////////////////
