frame_eqs_eel:
dec_m = pow (0.85, 30/fps);
dec_s = pow (0.9, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_s + beat*(1-dec_s);
is_beat = above(beat, 1.5*avg+2*peak) * above (time, t0+.3);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_m;
index = (index + is_beat) %32;
index2 = (index2 + is_beat*bnot(index)) %8;
q27 = index;
q24 = is_beat;

trig = is_beat;
vol1 = if(trig,.5+sqrt(peak)/4,vol1);
ran1 = if (trig, (rand(100)/50-1)*vol1, ran1);
ran2 = if (trig, (rand(100)/50-1)*vol1, ran2);

trig = is_beat * (index%8==0);
ran3 = if (trig, (rand(100)/50-1)*vol1, ran3);

ran1_ = ran1_*dec_m + (1-dec_m) * ran1;
ran2_ = ran2_*dec_m + (1-dec_m) * ran2;
ran3_ = ran3_*dec_m + (1-dec_m) * ran3;
dec_m = pow (0.7, 30/fps);

q10 = ran1_;
q11 = ran2_;
q12 = ran3_;

reg10 = 5000; reg11 = 40;
gmegabuf(reg10 + frame%reg11) = ran1_;
gmegabuf(reg10 + reg11 + frame%reg11) = ran2_;
gmegabuf(reg10 + 2*reg11 + frame%reg11) = ran3_;



//normalize spectrum
n = 0; vol = 0.1; mean = 0;
loop (flen,
  value = gmegabuf(n) * (1+1/(n+.5));
  dec = if(megabuf(n)<=value,0.2,pow(.95,30/fps));
  megabuf(n) = megabuf(n)*dec + (1-dec)*value;
  vol = vol + pow(megabuf(n),2);
  n= n+1;
);
vol = sqrt(vol);
vol_ = dec_m*vol_ + (1-dec_m)*vol;

// post smoothing
n= 0;
loop (flen,
  dec = if (megabuf(n)/vol > gmegabuf(n+1000),.5,.8);
  gmegabuf(n+1000) = gmegabuf(n+1000)*dec+ (1-dec)*megabuf(n)/vol_;
  n = n+1;
);


//Lookup-Tabele sortieren 
