float GetDist(float2 uvi) {float2 tmp; tmp = tex2D (sampler_pc_main, uvi).gb;
  return 1-(tmp.y + 1.0/255*tmp.x + ds*.7);}

float2 PutDist(float x) {float fg, fb; fg = modf((1-x)*255.0,fb);
  return (float2(fg,fb/255.0));}


float MinDist (float2 uvi)
{
   float tmp; float4 nb; float4 nb2; float2 pix;
   pix = texsize.zw*(1.0+length(uvi-0.5)*8.0);
   tmp = GetDist(uvi);
   nb.x = GetDist(uvi+pix*float2(-1,-1));
   nb.y = GetDist(uvi+pix*float2(1,-1));
   nb.z = GetDist(uvi+pix*float2(1,1));
   nb.w = GetDist(uvi+pix*float2(-1,1));
   nb2.x = GetDist(uvi+pix*float2(0,-1));
   nb2.y = GetDist(uvi+pix*float2(1,0));
   nb2.z = GetDist(uvi+pix*float2(0,1));
   nb2.w = GetDist(uvi+pix*float2(-1,0));
   //Eo.S. more efficient usage of min to get min value of all 8 GetDist() calls, removes 11ALU
   nb = min( nb, nb2 );
   nb.xy = min( nb.xy, nb.zw );
   tmp = min( nb.x, nb.y );
   return tmp;
}

float3 Get1 (float2 uvi) {float3 tmp; float2 pix;
