float4 mbox (float3 uvi) {float3 zz, zz0; float tmp, li;
  zz = uvi; zz0 = zz+q8; li=0;
  for (int n = 0; n <= 7; n++) {
    zz = 2.0*clamp(zz,-1,1)-zz;  tmp = dot(zz,zz);
    if (tmp <= 0.25) {zz *= 4; li=limit;} else if (tmp <= 1) {zz /= pow(tmp,1);}
    zz = 2.6*zz + zz0;}
  return float4(zz,li);}

