frame_eqs_eel:
warp=0;decay=1;wave_mystery=0;
vol = ((bass_att+mid_att+treb_att)/3+prevVol)/2;

// -------------------------------- Beat Detective 007 ----------------------------------
sure=if(equal(sure,0),.6,sure);
interval=if(equal(interval,0),40,interval);
lastbeat=if(equal(lastbeat,0),frame-FPS,lastbeat);
dbass=(bass-pbass)/FPS;
beat=above(dbass,.6*maxdbass)*above(frame-lastbeat,FPS/3);
sure=if(beat*below(abs(frame-(interval+lastbeat)),FPS/5),
min(.095+sure,1),beat*(sure-.095)+(1-beat)*sure*.9996);
sure=max(.5,sure);
cheat=if(above(frame,lastbeat+interval+ int(FPS/10))*above(sure,.91),1,cheat);
beat=if(cheat,1,beat);
sure=if(cheat,.95*sure,sure);
maxdbass=max(maxdbass*.999,dbass);
maxdbass=max(.012,maxdbass);
maxdbass=min(.02,maxdbass);
interval=if(beat, frame-lastbeat,interval);
lastbeat=if(beat,frame-cheat*int(FPS/10),lastbeat);
cheat=0;
pbass=bass;
// ----------------------------------------------------------------------------------------------
ob_size = below(frame-lastBeat,FPS/8)*.08*(frame-lastBeat)/FPS;
f=abs(cos(time/8+.54+sin(time/3+1.075)));
ob_r=1*f + 1*(1-f);
ob_g=.3*f + 1*(1-f);
ob_b=.3*f + .3*(1-f);

q1= 3.1416*(wave_mystery+1)/2;
q2=.25*cos(time+abs(2*sin(time*2+2.311)*( vol-amt))*sin(time*7.45+.876));
amt=amt+ .05*(vol-amt);
prevVol=vol;

pixel_eqs_eel:
x=x-.5;
y=-(y-.5);
ttan1 = tan(q1+1.5708)*x-y+q2;
ttan2 = tan(q1+1.5708);
IsLeftYtonosY = above(ttan1*(sign(3.1416-q1)),0);

dist = abs(ttan1)/sqrt(ttan2*ttan2 + 1);
totX=.5*cos(q1)*sign(IsLeftYtonosY-.5)*sqr(dist)/(.5-q2);
totY=-.5*sin(q1)*sign(IsLeftYtonosY-.5)*sqr(dist)/(.5-q2);

dx=dx+totX;
dy=dy+totY;
prevRot=q1;
