t6=pow( q1,3);  // interpolation parameter

waves.point_eqs_eel:
sample=.5*(0+sample);

ns=equal(t8%t1,0);

ccx = .85*sin(5.234+100*sample+t5)*cos(200*6.2831*sample+ 3.14*sample+2.45+t5);
ccy = 1.5*sin(100*sample+0.456+t5)*sin(100*6.2831*sample+ 3.14*sample+1.12+t5);
ccz = .85*sin(3.12+100*sample+t5)*cos(300*6.2831*sample +3.14*sample+.95+t5);

rr=.075+.067*abs(sin(6.2831*ccy));
ccx=ccx*(1-t6)+ (t3+rr*cos(sample*6.2831+6*time))*t6;
ccz=ccz*(1-t6)+ (t4+rr*sin(sample*6.2831+6*time))*t6;

cenx=(1-ns)*cenx+ns*(q4*ccx);
ceny=(1-ns)*ceny+ns*(q2+q4*abs(ccy));
cenz=(1-ns)*cenz+ns*(q3+q4*ccz);
central=equal(t8%2,0);
angle=6.2831*(t8-1)/t1;

ax=cenx+(1-central)*q4*t2*cos(angle+.12*ceny);
ay=ceny+(1-central)*q4*t2*sin(angle+.12*ceny);
az=cenz;
a=central*above(t8,0)*.07*above(ceny,q2);

invz=1/(az+100);
x=.5+5*ax*invz;
y=.5+5*ay*invz;

t8=(t8+1)%t1;

