warp_hlsl:
shader_body
{
    ret = tex2D( sampler_main, (uv-0.5)*1.00 + 0.5 );
    ret /= ret.x+ret.y+ret.z;
    float zoom = dot(ret, float3(1,0.975,0.95));

    ret.xyz = tex2D( sampler_main, (uv-0.5)*zoom + 0.5 );

    // .x = fastest layer, .z = slowest
    float r = 0.02; 
    float3 xfer = saturate((ret - 0.05)*99);
    xfer.yz *= saturate((0.1-ret.xy)*99);
    ret += xfer.xxx*float3(-1,1,0)*r*0.7;
    ret += xfer.yyy*float3(0,-1,1)*r*4;
    ret += xfer.zzz*float3(0,0,-1)*r;

    // ERROR DIFFUSION DITHER - looks great
    float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
    ret += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)/256.0*float3(1,3,8)*5;

}
