warp:
shader_body { 
 vec2 uv_1;
 vec2 tmpvar_2;
 tmpvar_2 = normalize((uv_orig - 0.5));
 float tmpvar_3;
 tmpvar_3 = (time * 7.0);
 uv_1 = (mix (uv_orig, uv, vec2((
   clamp ((((bass / bass_att) - 0.9) * 13.0), 0.0, 1.0)
  * 0.3))) + ((
   (tmpvar_2 * texsize.zw)
  * 
   cos(((rad * 170.0) - tmpvar_3))
 ) * 2.0));
 uv_1 = (uv_1 + ((
   (vec2(5.0, -5.0) * texsize.zw)
  * 
   cos(((ang * 30.0) - tmpvar_3))
 ) * tmpvar_2.yx));
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = ((texture (sampler_main, uv_1).xyz - 0.004) * 0.83);
 ret = tmpvar_4.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz + ((
   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1) * 3.0));
 ret_1 = (ret_1 + ((texture (sampler_blur2, 
   (((uv - 0.5) * 0.333) + 0.5)
 ).xyz * scale2) + bias2));
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    float t = saturate((bass/bass_att-0.9)*13);
    uv = lerp(uv_orig, uv, t*0.3);

    float2 v1 = normalize(uv_orig-0.5);
    float2 v2 = v1.yx * float2(1,-1);

    float z = length(texsize.zw)*450;

    uv.xy += v1*texsize.zw * cos(rad*170 - time*7)*2;
    uv.xy += v2*texsize.zw * cos(ang*30 - time*7)*5;

    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;



    // darken (decay) over time
    ret = (ret - 0.004)*0.83;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz*1 +
              GetBlur1(uv)*3 +
              GetBlur2(uv)*0 +
              GetBlur3(uv)*0 +
              0;
    ret += GetBlur2( (uv-0.5)*0.333 + 0.5);
    ret *= 1;
}
