ysw = equal((yseg+2)%2,0);


ox = x - xseg*(1/xnum);
ox = if(xsw,1/xnum - ox,ox);

oy = y - yseg*(1/ynum);
oy = if(ysw,1/ynum - oy,oy);

dx = above(xseg+yseg,0)*(x-ox);
