shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv);
 ret_1 = (tmpvar_2.xyz + ((tmpvar_2.xyz - 
   ((texture (sampler_blur2, uv).xyz * scale2) + bias2)
 ) * 0.3));
 ret_1 = (ret_1 * 0.9);
 ret_1 = (ret_1 + ((
   ((texture (sampler_noise_lq, ((
