rot = rot + 0.5*sin(0.5-rad)*cos(0.02*(0.5-rad)+time);
sx = sx + 0.01*(0.99*1-rad)*sin(0.733*time)*below(sin(time),0);
sy = sy + 0.01*(0.99*1-rad)*cos(0.953*time)*above(sin(time),0);
