warp:
shader_body { 
 vec3 tmpvar_1;
 tmpvar_1 = max (texture (sampler_fc_main, uv).xyz, (texture (sampler_fc_main, mix (uv, uv_orig, vec2(0.33, 0.33))).xyz * 0.95));
 vec2 x_2;
 x_2 = (uv - uv_orig);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = (((
   mix (tmpvar_1, (floor((
     (tmpvar_1 * vec3(7.0, 6.0, 8.0))
    + 0.25)) / vec3(7.0, 6.0, 8.0)), vec3(0.1, 0.1, 0.1))
  * 0.99) - 0.0025) - ((1.0 - 
   clamp ((sqrt(dot (x_2, x_2)) * 40.0), 0.0, 1.0)
 ) * 0.003));
 ret = tmpvar_3.xyz;
}

comp:
shader_body { 
 vec2 tmpvar_1;
 tmpvar_1.x = cos((13.5 * uv.y));
 tmpvar_1.y = sin((19.5 * uv.x));
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = max (texture (sampler_main, uv).xyz, texture (sampler_main, (uv + (0.06 * tmpvar_1))).xyz);
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame, with some trails...
    float trail_decay = 0.95;
    ret = tex2D( sampler_fc_main, uv ).xyz;
    float3 s2 = tex2D( sampler_fc_main, lerp(uv,uv_orig,0.33) ).xyz*trail_decay;
    ret = max(ret, s2);

    // a color-holding posterize effect:  
    float3 m = float3(7,6,8);
    ret = lerp(ret, floor(ret*m+0.25)/m, 0.1);


    // darken over time
    float center = 1-saturate(length(uv-uv_orig)*40);
    ret = (ret*0.99 - 0.0025 - center*0.003); 

}

