frame_eqs_eel:
warp = 0;

ob_r = ob_r + 0.8*sin(1.1*time) + 1.1*cos(2*time);
ob_b = ob_b + 0.9*sin(1.2*time) + cos(1.5*time);
ob_g = ob_g + 0.7*sin(time) + sin(0.4*time);
wave_r = abs(ob_r+0.2);
wave_g = abs(ob_g+0.2);
wave_b = abs(ob_b+0.2);
ib_b = ib_b + 0.44*cos(1.5*time) + 0.7*tan(time);
ib_g = ib_g + 2*sin(time*bass_att);
ib_r = ib_r + 0.8*sin(1.2*time) + 1.3*cos(1.8*time);

pixel_eqs_eel:
thresh = above(bass_att,thresh)*2+(1-above(bass_att,thresh))*((thresh-1.3)*0.96+1.3);
dx_r = equal(thresh,2)*0.015*sin(5*time)+(1-equal(thresh,2))*dx_r;
dy_r = equal(thresh,2)*0.015*sin(6*time)+(1-equal(thresh,2))*dy_r;
zoom = zoom + 0.07*treb*(abs(sin(time*rad)))*(0.3-rad);
dy = dy + dy_r*below(rad,0.5 - (0.2*time));
dx = dx + dx_r*below(rad,0.5 - (0.2*time));
rot = rot + (0.7*bass_att*sin(abs(dx_r*(1-rad)*2*time+cos(rad*12))))*0.2;

warp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv);
 ret_1 = (tmpvar_2.xyz + ((tmpvar_2.xyz - 
   ((texture (sampler_blur1, uv).xyz * scale1) + bias1)
 ) * 0.3));
 ret_1 = (ret_1 * 0.9);
 ret_1 = (ret_1 + ((
   (texture (sampler_noise_lq, ((uv_orig * texsize.xy) * (texsize_noise_lq.zw * 0.1))).xyz - 0.5)
  / 256.0) * 28.0));
 vec3 tmpvar_3;
 tmpvar_3 = mix (ret_1, vec3(dot (ret_1, vec3(0.32, 0.49, 0.29))), vec3(0.04, 0.04, 0.04));
 ret_1 = tmpvar_3;
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = tmpvar_3;
 ret = tmpvar_4.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // take the difference between the crisp and blurred images,
    // then add it back into the image.  Creates spots and stripes over time.
    ret += (ret - GetBlur1(uv))*0.3;
    ret *= 0.9;

   // add noise:
   float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 0.1;// + rand_frame.xy;
   ret += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)/256.0 * 28;

   // desaturate over time, to keep the globs white
   ret = lerp(ret, lum(ret), 0.04);
}
