warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // take the difference between the crisp and blurred images,
    // then add it back into the image.  Creates spots and stripes over time.
    ret += (ret - GetBlur2(uv))*0.3;
    ret *= 0.9;

   // add noise:
   float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 0.4 + rand_frame.xy;
   ret += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)/256.0 * 122 * saturate(treb_att-1);

   // desaturate over time, to keep the globs white
   ret = lerp(ret, lum(ret), 0.2);
}

comp_hlsl:
shader_body
{
    float2 uv2;

    float4 c = float4(1,0.4,0.15,0.1);

    uv2 = uv + float2(1,0)*texsize.zw;
    float gx1 = GetPixel(uv2)*c.x + GetBlur1(uv2)*c.y + GetBlur2(uv2)*c.z + GetBlur3(uv2)*c.w;
    uv2 = uv + float2(-1,0)*texsize.zw;
    float gx2 = GetPixel(uv2)*c.x + GetBlur1(uv2)*c.y + GetBlur2(uv2)*c.z + GetBlur3(uv2)*c.w;

    uv2 = uv + float2(0,1)*texsize.zw;
    float gy1 = GetPixel(uv2)*c.x + GetBlur1(uv2)*c.y + GetBlur2(uv2)*c.z + GetBlur3(uv2)*c.w;
    uv2 = uv + float2(0,-1)*texsize.zw;
    float gy2 = GetPixel(uv2)*c.x + GetBlur1(uv2)*c.y + GetBlur2(uv2)*c.z + GetBlur3(uv2)*c.w;

    float3 g = normalize(float3(gx1-gx2, gy1-gy2, 0.14));

    ret = 0.5 + 0.5*g;
    ret *= saturate(length(ret.xy-0.5)*5);
    ret = ret.xxy;
    ret += 1.15;
    ret *= lerp(ret,ret*(GetBlur3(uv)-GetBlur1(uv)),pow(hue_shader.yxz,ret));
    ret *= ret;

    //ret *= 1.5; //old gamma effect    
}
