 vec3 ret_1;
 ret_1 = texture (sampler_main, uv).xyz;
 ret_1 = (ret_1 + ((ret_1 - 
   ((texture (sampler_blur2, uv).xyz * scale2) + bias2)
 ) * 0.3));
 ret_1 = (ret_1 * 0.9);
 ret_1 = (ret_1 + ((
   ((texture (sampler_noise_lq, ((
     (uv_orig * texsize.xy)
    * 
     (texsize_noise_lq.zw * 0.4)
   ) + rand_frame.xy)).xyz - 0.5) / 256.0)
  * 122.0) * clamp (
