frame_eqs_eel:
warp=0;
chaos=.9+.1*sin(pulse);
entropy=if(bnot(entropy),2,if(equal(pulse,-20),1+rand(5),entropy));
bass_thresh = above(bass_att,bass_thresh)*2 + (1-above(bass_att,bass_thresh))*((bass_thresh-1.3)*chaos+1.3);
bass_changed=abs(bass_changed-equal(bass_thresh,2));
treb_thresh=above(treb_att,treb_thresh)*2 + (1-above(treb_att,treb_thresh))*((treb_thresh-1.3)*chaos+1.3);
treb_changed=abs(treb_changed-equal(treb_thresh,2));
mid_thresh=above(mid_att,mid_thresh)*2 + (1-above(mid_att,mid_thresh))*((mid_thresh-1.3)*chaos+1.3);
mid_changed=abs(mid_changed-equal(mid_thresh,2));
pulse=if(above(abs(pulse),20),-20,pulse+.1*chaos*bor(bor(bass_changed,treb_changed),mid_changed)+(mid+bass+treb)*.025);
q3=sin(pulse);
q2=(pulse/entropy)*.5*chaos;
q4=sin(q2);
q5=entropy;
q1=(1+1*above(q4,0))*(1+2*above(q3,0))*(1+4*mid_changed*above(q3,0))*(1+6*above(pulse,0));
wave_r=if(treb_changed,.5+.5*q3,if(bass_changed,.5+.5*q4,1));
wave_g=.5+.2*bnot(q1%2)-.2*bnot(q1%3)+.2*bnot(q1%5)-.2*bnot(q1%7);
wave_b=if(bnot(q1%6),.8+.2*q4,bass_changed*mid_changed);
ob_r=ob_r+.2*q4+.3*bnot(q1%7)*q3;
ob_b=ob_b-.1*bnot(q1%105)-.4*sin(q2*.8);
ob_g=ob_g+.5*sin(pulse*.4*entropy);
ob_a=.07+.05*q3;
ob_size=.01*entropy*bnot(q1%6);
ib_r=ib_r+.2*q1-.3*bnot(q1%3)*q4;
ib_b=ib_b-.1*bnot(q1%42)-.4*sin(q2*.7);
ib_g=ib_g+.5*sin(pulse*.5*entropy);
ib_a=.07+.05*q3*q4;
ib_size=.005+.005*q3;
zoom_fade=if(above(q3,0),if(above(q4,0),zoom_fade-.0013,zoom_fade+.002),1+.04*q4);
zoom=zoom_fade;

pixel_eqs_eel:
g1=sin(q2*.04*q5);
g2=sin(q2*.05*q5);
g3=sin(q2*.06*q5);
x_shift=pow(x,2)+x*g1*2+sqr(g1);
y_shift=pow(y,2)+y*g2*2+sqr(g2);
r_shift=pow(rad,2)+rad*g3*2+sqr(g3);
zoom=zoom -sin(x_shift*bnot(q1%10)+y_shift*bnot(q1%14)+r_shift*bnot(q1%21))*q3*(.1+.1*bnot(q1%30)+.1*bnot(q1%7));
dx=dx+bnot(q1%35)*(rad-.5*g3)*g2-cos(y*1.68)*.1*bnot(q1%21);
dy=dy-cos(x*1.68)*bnot(q1%10);
rot=if(above(sin(q2*4.3),0),.3*above(abs(zoom-1),.05),.3*g3);
