warp_hlsl:
shader_body
{
    // PAINTERLY EFFECT:
    float2 t = 0.005 + 0.045*saturate(float2(bass,treb)-1);
    float2 uv2 = uv;
    float2 delta2 = texsize.zw*float2(1,1);
    float3 blurry_color = tex2D( sampler_main, 
        lerp(uv,uv_orig,-1) + delta2 ).xyz;
    uv2.xy += (blurry_color.xy-0.37) * t;
    //uv2.xy -= (blurry_color.zx-0.37) * s;

    // sample previous frame
    ret = tex2D( sampler_main, uv2 ).xyz;

    // darken over time
    ret -= 0.001;    
}

comp_hlsl:
float2 uv_x, uv_y, uv_z, uv_echo;
shader_body
{
    float rad_hq = length(uv-0.5);
    float2 d = texsize.zw*4;
    float f = 0.7;
    float3 N;
    N.x = ( GetBlur2(uv + float2(1,0)*d) - GetBlur2(uv-float2(1,0)*d) ).x*f;
    N.y = ( GetBlur2(uv + float2(0,1)*d) - GetBlur2(uv-float2(0,1)*d) ).x*f;
    N.x += ( GetBlur1(uv + float2(1,0)*d) - GetBlur1(uv-float2(1,0)*d) ).x*(1-f);
    N.y += ( GetBlur1(uv + float2(0,1)*d) - GetBlur1(uv-float2(0,1)*d) ).x*(1-f);
    N.z = -0.05;
    N = normalize(N);

    ret = N;

    float3 light_pos = float3(q6,q7,-0.8);


    float3 P = float3(uv_orig.xy*2-1, 0);
    float3 L = normalize(light_pos - P);
    float3 E = float3(0,0,1);
    float3 V = normalize(P-E);
    float3 R = normalize( V + 2*N*dot(V,N) );

    // diffuse lighting:
    ret = saturate(dot(N,L));
    //ret *= float3(1.2,0.9,0.6);    
    ret *= normalize( GetBlur3(uv_orig) + 0.05 );
    //ret = 0;

    // spec lighting:
    ret += pow(saturate(dot(R,L)),16)*0.5;

    //ret *= 0.7;
}
