warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // take the difference between the crisp and blurred images,
    // then add it back into the image.  Creates spots and stripes over time.
    ret += (ret - GetBlur2(uv))*0.6;
    ret *= 0.9;

   // add noise:
   float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 0.4 + rand_frame.xy;
   ret += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)/256.0 * 12 * saturate(treb_att-1);

   // desaturate over time, to keep the globs white
   ret = lerp(ret, lum(ret), 0.04);
}

comp_hlsl:
float2 uv_x, uv_y, uv_z, uv_echo;
shader_body
{
    float rad_hq = length(uv-0.5);
    float2 d = texsize.zw*1;
    float f = 0.8;
    float3 N;
    float2 uv2 = uv_orig;
    N.x = ( GetPixel(uv2 + float2(1,0)*d) - GetPixel(uv2-float2(1,0)*d) ).x*f;
    N.y = ( GetPixel(uv2 + float2(0,1)*d) - GetPixel(uv2-float2(0,1)*d) ).x*f;
    N.x += ( GetBlur1(uv2 + float2(1,0)*d) - GetBlur1(uv2-float2(1,0)*d) ).x*(1-f);
    N.y += ( GetBlur1(uv2 + float2(0,1)*d) - GetBlur1(uv2-float2(0,1)*d) ).x*(1-f);
    N.z = -0.077;
    N = normalize(N);

    ret = N;

    float3 light_pos = float3(q9,q10,-0.8);


    float3 P = float3(uv_orig.xy*2-1, 0);
    float3 L = normalize(light_pos - P);
    float3 E = float3(0,0,1);
    float3 V = normalize(P-E);
    float3 R = normalize( V + 2*N*dot(V,N) );

    // diffuse lighting:
    ret = saturate(dot(N,L));
    ret *= float3(1.2,0.9,0.7);    
    ret *= normalize( GetBlur3(uv_orig).yzx );
    //ret = 0;

    // spec lighting:
    ret += pow(saturate(dot(R,L)),32)*0.5;

    //ret *= 0.7;
}
