shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv);
 ret_1 = (tmpvar_2.xyz + ((tmpvar_2.xyz - 
   ((texture (sampler_blur1, uv).xyz * scale1) + bias1)
 ) * 0.2));
 ret_1 = (ret_1 * 0.9);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

comp:
shader_body { 
 float ang2_1;
 ang2_1 = ((ang * 0.1591549) + (time * 0.025));
 float tmpvar_2;
 tmpvar_2 = (3.0 + floor((rand_preset.z * 5.95)));
 ang2_1 = (fract((ang2_1 * tmpvar_2)) / tmpvar_2);
 ang2_1 = (abs((ang2_1 - 
   (0.5 / tmpvar_2)
 )) * 6.283185);
 vec2 tmpvar_3;
 tmpvar_3.x = cos(ang2_1);
 tmpvar_3.y = sin(ang2_1);
 vec4 tmpvar_4;
