warp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz + ((
   (texture (sampler_noise_lq, (((uv_orig * texsize.xy) * texsize_noise_lq.zw) + rand_frame.xy)).xxx - 0.5)
  / 256.0) * 2.0));
 vec2 x_2;
 x_2 = (uv - uv_orig);
 ret_1 = (ret_1 * (0.97 + (0.03 * 
   clamp ((sqrt(dot (x_2, x_2)) * 200.0), 0.0, 1.0)
 )));
 vec4 tmpvar_3;
