 vec3 ret_1;
 vec2 tmpvar_2;
 tmpvar_2 = (uv - 0.5);
 ret_1.x = texture (sampler_main, (tmpvar_2 + 0.5)).x;
 ret_1.y = texture (sampler_main, ((tmpvar_2 * 0.98) + 0.5)).y;
 ret_1.z = texture (sampler_main, ((tmpvar_2 * 0.95) + 0.5)).z;
 vec3 tmpvar_3;
 tmpvar_3.x = -(ret_1.x);
 tmpvar_3.y = (ret_1.x - ret_1.y);
 tmpvar_3.z = (ret_1.y - ret_1.z);
 ret_1 = (ret_1 + (tmpvar_3 * 0.03));
 ret_1 = (ret_1 + ((
   (texture (sampler_noise_lq, (((uv_orig * texsize.xy) * texsize_noise_lq.zw) + rand_frame.xy)).xyz - 0.5)
  / 256.0) * 3.0));
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = ret_1;
 ret = tmpvar_4.xyz;
}

