static const float4x4 tab1 = .3*float4x4 (0,2,1,-4,  3,0,1,5,  1,-3,2,-4,  -4,1,2,0) ;

float aTan2(float y, float x) {float angle;	 float abs_y = abs(y);
if (x >= 0) {float r = (x - abs_y) / (x + abs_y); angle = 1-r;}
else {float r = (x + abs_y) / (abs_y - x); angle = 3-r; }
angle = angle * M_PI/4.0;
return y < 0 ? -angle : angle;}

float2 GP (float2 uvi) {return tex2D (sampler_pc_main, uvi).gb;}

float2 fstep2 (float2 xy) {return 0.0039216*round(255.0*xy);}
float4 fstep4 (float4 xy) {return round(255.0*xy);}

float GetDist(float2 uvi) {float2 tmp; tmp = fstep2(tex2D (sampler_pc_main, uvi).gb);
return 1.0078125-tmp.y -0.015625*tmp.x;} // = 1-(tmp.y + 1.0/res2*(tmp.x-.50));}

float2 PutDist(float x) {float tmp; x = 1-x; tmp = fstep2(x);
  return (float2(64.0*(x-tmp)+.495,tmp));}

float tdist (float d) {return d + pow(d,5)*0;}

float MinDist (float2 uvi)
{  float tmp; float4 na; float4 nb; float4 nc; float4 nd; float2 pix;
   pix = texsize.zw;
   na = fstep4(float4(GP(uvi+pix*float2(0,1)),  GP(uvi+pix*float2(-1,0))));
   nb = fstep4(float4(GP(uvi+pix*float2(-1,-1)),GP(uvi+pix*float2(1,-1))));
   nc = fstep4(float4(GP(uvi+pix*float2(1,1)),  GP(uvi+pix*float2(-1,1))));
   nd = fstep4(float4(GP(uvi+pix*float2(0,-1)), GP(uvi+pix*float2(1,0))));
   //Eo.S. more efficient usage of min to get min value of all 8 GetDist calls, removes 11ALU
   na = float4 (na.yw + 0.015625*na.xz, nb.yw + 0.015625*nb.xz);
   nb = float4 (nc.yw + 0.015625*nc.xz, nd.yw + 0.015625*nd.xz);
   na    = max(na,nb);
   na.xy = max(na.xy,na.zw);
   return 1.0078125 -  max(na.x, na.y)*0.0039216;}

float3 Get1 (float2 uvi) {return lerp (GetPixel(uvi), GetBlur1(uvi),0);}

float3 mbox (float3 uvi) {float3 zz, zz0, zzlo; float2 uv3; float tmp, ang1, len, mod1;
  uv3 = floor(uvi.xy+63.99);
  mod1 = tab1[uv3.x%4][uv3.y%4];

  uvi.xy = 1- abs( frac((uvi.xy-.5) * 0.5 ) * 2 - 1.0 ); // Eo.S. mirror code
  ang1 = aTan2(uvi.y,uvi.x); len = length(uvi.xy)*7.5;
  uvi.z -= mod1;
  len *= 1-.3*floor(12*uvi.z*mod1+q19);
  zz = float3 (len, ang1, uvi.z);
  zz0 = zz;
  for (int n = 0; n <= 7; n++) {
    zz = 2.0*clamp(zz,-1,1)-zz;  tmp = dot(zz,zz);
    zz *= saturate(max(.25/tmp, .25))*4;
    zz = ffac*zz + zz0; if (n == 5) {zzlo = zz;}}
return float3(length(zzlo), length(zz), (ang1-mod1-frac(zz.y*.0001))*.3);}

