  megabuf(n) = BPM;
  index += 1;
);


//structure of resonator data, starting at megabuf (0)
//size = 8
//0: BPM
//1: v
//2: y
//3: amplitude
//4: amplitude after harmonics post processing
//5: beatcos
//6: beatsin


pi = 3.1416;
reg00 = num_res;

frame_eqs_eel:
dt = min (1/fps,0.1) ; //Stability !
dec_m = 1 - 4*dt; dec_s = 1 - 1.25*dt;
vol =  (bass+mid+treb); 

vol_ = vol_*dec_m + (1-dec_m)*vol; exc = vol-vol_;
excite = atan (exc*2)/1.6;

index=0; ratio = log(maxbpm/minbpm); maxquad = .5; meanquad = 0;
loop (num_res,
  n = index*8;
  omega      =   megabuf(n) * 6.2832/60;
  megabuf (n+1) *= 1-0.22*dt; //damping
  EC_Steps = int (10*omega*dt)+1; 
  loop (EC_Steps, // Euler-Cauchy steps
    megabuf (n+1) += (excite - sqr(omega)*megabuf(n + 2)) * dt/EC_Steps;
    megabuf (n+2) += megabuf(n+1) * dt/EC_Steps);
  beatcos  = megabuf(n+1);  
  beatsin = megabuf(n+2)*omega; //2pi/60
  quad     = sqrt (pow(beatsin,2) + pow(beatcos,2)) ;
  if (maxquad < quad, maxquad = quad, 0);
  meanquad += quad / num_res;
  megabuf (n+3) = megabuf(n+3) *dec_s +  quad* (1-dec_s);
  megabuf (n+4) = megabuf(n+3);
  megabuf (n+5) = beatcos;
  megabuf (n+6) = beatsin ;
  index += 1;
);

n = 0; maxind = 0;
loop (num_res,
  scale =  (1+megabuf(n+3)/2)/3; 
  if (megabuf(n+3) > megabuf(maxind+3), maxind = n, 0);
  gmegabuf(n+5) = megabuf(n+5) * scale;
  gmegabuf(n+6) = megabuf(n+6) * scale;
n+=8;);


sig0 = sig;
sig = megabuf(maxind+6);
gp = (maxquad* (1+gp*0.3) > 3.5*meanquad );
