prog2 += (sig*sig0 <0) * gp2;
q28 = prog2/3;
flash = if ((sig*sig0 < 0)* (maxquad > 8.5*meanquad),4, flash * 0.5) ;
q29 = flash;
maxind0 = maxind;

n = 0; imax = 0;
loop (num_res,
  q12 += megabuf(n*8+1);
  q13 += megabuf(n*8+2);
n+=1;);

maxquad_ = maxquad_*dec_m + (1-dec_m)*maxquad;
q1 = sqrt(3/maxquad_);
tr2 += maxquad_/fps;
q2 = tr2*1.85; //vert. mov. muster

