//rota = .2*sin(wamp_*32);
q5 = cos(rota);
q6 = sin(rota);
q7 = -q6;
q8 = q5;

q15 = -sin(trel*.5)*.05;
q16 = cos(trel)*wamp_/2;

q19 = .1*sin(trel/12);

//ODD PLANET MOVEMENT
trig1 = bnot(vis)*bnot(rand(100*fps));
vis = if (trig1,1,vis*below(mov1,1));
q30 = vis;
mov1 = if (vis,mov1+2*speed/fps,-1);
dir = if(trig1,rand(100),dir); 
q31 = .5+.5*cos(dir)*mov1;
q32 = .5+.5*sin(dir)*mov1;

q29 = (2+sin(time/17)); //reflection water

