shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = (texture (sampler_main, (uv + (
   (texture (sampler_main, (uv + texsize.zw)).xy - 0.37)
  * 0.03))).xyz - 0.004);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec2 uv_1;
 vec3 ret_2;
 uv_1 = (0.05 + (0.9 * uv));
 vec2 tmpvar_3;
 tmpvar_3 = (((uv_1 - 0.5) * vec2(-1.0, 1.0)) + 0.5);
 ret_2 = (abs((
   ((texture (sampler_blur1, uv_1).xyz * scale1) + bias1)
  - 
   mix (texture (sampler_main, uv_1).xyz, texture (sampler_main, tmpvar_3).xyz, vec3(0.5, 0.5, 0.5))
 )) * 6.0);
 ret_2 = (ret_2 * (0.1 + abs(
   max (sin(bass_att), sin(treb_att))
 )));
 ret_2 = (ret_2 * max ((
   (texture (sampler_blur3, uv_1).xyz * scale3)
  + bias3), (
   (texture (sampler_blur3, tmpvar_3).xyz * scale3)
  + bias3)));
 ret_2 = (ret_2 * 1.5);
 ret_2 = (ret_2 - (roam_sin.xyz * roam_cos.zxy));
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = ret_2;
 ret = tmpvar_4.xyz;
}

warp_hlsl:
shader_body
{
    // PAINTERLY EFFECT:
    float2 uv2 = uv;
    const float mipbias = 0;
    float2 delta2 = texsize.zw*float2(1,1);
    float3 blurry_color = tex2D( sampler_main, uv2 + delta2 ).xyz;
    uv2.xy += (blurry_color.xy-0.37) * 0.03;

    // sample previous frame
    ret = tex2D( sampler_main, uv2 ).xyz;

    // darken over time
    ret -= 0.004;
}

comp_hlsl:
shader_body
{
    uv = 0.05 + 0.9*uv;


    float2 uv_echo = (uv - 0.5)*float2(-1,1) + 0.5;
    ret = lerp( tex2D(sampler_main, uv).xyz, 
                tex2D(sampler_main, uv_echo).xyz, 
                0.50 
              ); //video echo
